function generateFromVocab(vocab) {
  return vocab.map((item) => ({
    id: `gen-${item.id}`,
    type: "vocab",
    level: item.level,
    topic: item.topic,
    prompt: `Traduce: ${item.es}`,
    answer: item.en,
    note: "Generado desde vocabulario",
  }));
}

